function s(r,t){const e=r.replace(/([a-z])([A-Z])/g,"$1 $2").replace(/[_-]+/g," ").replace(/\s+/g," ").trim().toLowerCase().split(" ").filter(Boolean);switch(t){case"sentence":return e.length===0?"":(e[0]?.charAt(0).toUpperCase()??"")+(e[0]?.slice(1)??"")+(e.length>1?" "+e.slice(1).join(" "):"");case"upper":return r.toUpperCase();case"lower":return r.toLowerCase();case"pascal":return e.map(a=>a.charAt(0).toUpperCase()+a.slice(1)).join("");case"snake":return e.join("_");default:throw new Error(`Unsupported case style: ${t}`)}}export{s as t};
